class Mempool:
    connection = "https://bsc-dataseed.binance.org/"
    contract_address = "0x10ed43c718714eb63d5aa57b78b54704e256024e"

    def __init__(self, connection: str = connection, contract_address: str = contract_address, abi = pancakeabi):
        self.connection = Web3(Web3.HTTPProvider(connection))
        self.contract_address = contract_address
        self.address_checksum = Web3.toChecksumAddress(self.contract_address)
        self.contract = self.connection.eth.contract(address=self.address_checksum, abi=abi)
        # print(self.contract.all_functions())

    def __pool_data(self):
        pending_pool = self.connection.geth.txpool.content()['pending']
        # print(self.connection.geth.txpool.content()['queued'])
        # print(pending_pool)
        pool_json = Web3.toJSON(pending_pool)
        # print(pool_json)
        return pool_json

    def explore(self):
        pool_data = json.loads(self.__pool_data())

        results = []
        for pool in pool_data.values():
            to = list(pool.items())[0][1]['to']
            if self.contract_address == to:
                results.append(list(pool.items())[0][1])
                # print(list(pool.items())[0][1])

        filtered_results = [list(pool.items())[0][1]
                            for pool in pool_data.values()
                            if self.contract_address in list(pool.items())[0][1]['to']]
        # print(results)
        return results

    def filter_method(self, method_id: str):
        pool_data = json.loads(self.__pool_data())
        pool_info = [(list(pool.items())[0][1]['hash'],
                      self.contract.decode_function_input(list(pool.items())[0][1]['input'])[1])
                     for pool in pool_data.values()
                     if self.contract_address in list(pool.items())[0][1]['to'] and
                     method_id in list(pool.items())[0][1]['input']]
        return pool_info


mem = Mempool()
txns = mem.explore()

for txn in txns:
    txnHash = txn['hash']
    print(txnHash)

    t = mem.connection.eth.get_transaction(txnHash)
    print(t['input'])
    txnInput = mem.contract.decode_function_input(t['input'])
    print(txnInput)

    txn_mined = False
    count = 0
    while not txn_mined:
        try:
            receipt = mem.connection.eth.get_transaction_receipt(txnHash)
            print(receipt.status)
            txn_mined = True
        except:
            time.sleep(0.5)
            count = count + 1
            print('not yet')
            print(count)

            if count > 5:
                count = 0
                break
            else:
                pass

